'use client'
export const dynamic = 'force-dynamic'

import { useSuspenseQuery } from '@apollo/experimental-nextjs-app-support/ssr'
import { gql } from '@apollo/client'
import { Image } from 'antd'
import ReactHtmlParser from 'react-html-parser'
import { useState } from 'react'
import './styles.sass'

const GET_PRODUCT_BY_ID = gql`
  query getProductById($slug: String!) {
    produto(where: { slug: $slug }) {
      id
      nome
      slug
      atributos {
        nome
      }
      descricao {
        html
      }
      imagens {
        url
      }
    }
  }
`

export default function ProductDetailsPage({ params }) {
  const [productAttribute, setProductAttribute] = useState()
  const [productQuantity, setProductQuantity] = useState()
  const [productObservation, setProductObservation] = useState(null)

  const { data } = useSuspenseQuery(GET_PRODUCT_BY_ID, {
    variables: { slug: params.slug }
  })

  function requestContact(event) {
    const productRequestData = {
      name: data.produto.nome,
      attribute: productAttribute,
      quantity: productQuantity,
      observation: productObservation || null
    }

    let storagedProduct = []

    if (typeof window !== 'undefined') {
      if (localStorage.getItem('products') !== null) {
        storagedProduct = JSON.parse(localStorage.getItem('products'))
        storagedProduct.push(productRequestData)
        localStorage.setItem('products', JSON.stringify(storagedProduct))
        console.log('produto adicionado ao carrinho')
      } else {
        storagedProduct.push(productRequestData)
        localStorage.setItem('products', JSON.stringify(storagedProduct))
        console.log('carrinho criado')
      }
    } else {
      return
    }

    event.preventDefault()
  }

  return (
    <main>
      <section className="product-top-section">
        <div className="product-image-container">
          {data.produto?.imagens ? (
            <Image
              src={data.produto.imagens[0]?.url}
              className="product-image"
            />
          ) : (
            <Image
              width={540}
              src="error"
              fallback="data:image/png;base64,"
            />
          )}
        </div>

        <form onSubmit={requestContact}>
          <div className="product-infos-container">
            <h1 className="product-name">{data.produto.nome}</h1>

            <div className="description-container">
              {ReactHtmlParser(data.produto.descricao.html)}
            </div>

            {data.produto.atributos && (
              <div className="attributes-container">
                <p>Selecione uma opção:</p>
                <select
                  required
                  onChange={(event) => {
                    setProductAttribute(event.target.value)
                  }}
                  className="product-selector"
                >
                  {data.produto.atributos.map((element, index) => {
                    return (
                      <option
                        value={element.nome}
                        key={index}
                        defaultValue={element.nome}
                      >
                        {element.nome}
                      </option>
                    )
                  })}
                </select>
              </div>
            )}

            <div className="quantity-container">
              <p>Informe a quantidade:</p>
              <input
                type="number"
                name=""
                id=""
                onChange={(event) => {
                  setProductQuantity(event.target.value)
                }}
                className="product-selector"
                required
              />
            </div>

            <div className="observation-container">
              <textarea
                type="text"
                placeholder="Observações (opcional)"
                onChange={(event) => setProductObservation(event.target.value)}
                className="observation-input"
              ></textarea>
            </div>

            <input
              type="submit"
              value="Solicitar Orçamento"
              className="purchase-button"
            />
          </div>
        </form>
      </section>
    </main>
  )
}
